mapper.update:
UPDATE 
	board 
SET 
	board_title = #{boardTitle},
    board_content = #{boardContent} 
WHERE 
	board_no = #{boardNo} 
AND 
	board_pw = #{boardPw}

mapper.delete:
DELETE FROM 
	board 
WHERE 
	board_no = #{boardNo} 
AND 
	board_pw = #{boardPw}

mapper.insert:
	
        SELECT 
        	LAST_INSERT_ID()
    
    INSERT INTO 
    	board(
        	board_pw
        	, board_title
        	, board_content
        	, board_user
        	, board_date
        ) 
    VALUES
    (
        #{boardPw}
        , #{boardTitle}
        , #{boardContent}
        , #{boardUser}
        , now()
    )
